import '../CSSContents/Cart.css'

function Cart({
    visibilty,
    items,
    onProductRemove,
    onClose,
    onQuantityChange, }) {

    return (
        <div className='Cart'>

            <div>
                <h2>Shopping Cart</h2>
                <button
                    className="btn close-btn"
                    onClick={onClose}>
                </button>
            </div>

            <div className='cart-item'>
                {items.length === 0 && (<span className='empty-text'>no item is selected</span>)}
                {items.map((item) => (
                    <div className="cart-product"
                        key={product.id}>
                        <img
                            src={
                                item.image
                            }
                            alt={item.name}
                        />
                    </div>
                ))}
            </div>
        </div>
    )
}




export default Cart;
