import "../CSSContents/ForgetPassword.css";
import { useState } from "react";
import axios from 'axios';

function ForgetPassword() {
  const [email, setEmail] = useState({ email: '',});
  const [message, setMessage] = useState('');

  

const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:4000/ForgetPassword', email)
                    .then((response) => {
                        setMessage('Logged in successfully');
                    })

  };

  return (
    <div className="ForgetPassword">
      <form onSubmit={handleSubmit}>
        <h2>Forget Password</h2>
        <p>Enter your email address below and we will send you a link to reset your password.</p>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" className="form-control" placeholder="Enter email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <p>Enter your new Password.</p>
        <div className="form-group">
          <label>Email:</label>
          <input type="text" className="form-control" placeholder="Enter NewPassword" value={email} onChange={(e) => setPassword(e.target.value)} />
        </div>
 
        <button type="submit" className="btn btn-primary">Submit</button>
        {message && <p className="message">{message}</p>}
      </form>
    </div>
  );
}

export default ForgetPassword;
