import "../CSSContents/newLog.css";
import React, { useState } from "react";
import axios from 'axios';




function Login2() {


    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [loginStatus, setLoginStatus] = useState("");
    const [registerStatus, setRegisterStatus] = useState("");

    const login = (e) => {
        e.preventDefault();
        axios.post("http://localhost:3000/login", {
            username: username,
            password: password,
        }).then((response) => {
            if (response.data.message) {
                setLoginStatus(response.data.message)
            } else {
                setLoginStatus(response.data[0].email)
            }
        })
    }

    const register = (e) => {
        e.preventDefault();
        axios.post("http://localhost:3000/register", {
            username: username,
            email: email,
            password: password,
        }).then((response) => {
            if (response.data.message) {
                setRegisterStatus(response.data.message)
            } else {
                setRegisterStatus("Acoount created successfully")
            }
        })
    }

    return (
        <div className="App">
            <form >
                <h4>Login Here</h4>
                <label>Username</label>
                <input type="text" onChange={(e) => { setUsername(e.target.value) }} placeholder="your username" />
                <label>Password</label>
                <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="your password" />
                <button onChlick={login} value="Login">submit</button>
                <h1 >{loginStatus}</h1>
            </form>
            <br />
            <form>
                <h4>Register Here</h4>
                <label>Username</label>
                <input type="text" onChange={(e) => { setUsername(e.target.value) }} placeholder="Enter your Email Address" />
                <label>Email</label>
                <input type="text" onChange={(e) => { setEmail(e.target.value) }} placeholder="your username"></input>
                <label>Password</label>
                <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="Enter your Password"></input>
                <button onChlick={register} value="Create an account" >submit</button>
                <h1 >{registerStatus}</h1>
            </form>
        </div>
    );
};

export default Login2;
