import '../CSSContents/Cart.css'


import React from "react";

function Cart({ items, onProductRemove, onClose, onQuantityChange }) {
    return (
        <div className="modal">
            <div className="shoppingCart">
                <div className="header">
                    <h2>Shopping cart</h2>
                    <button className="btn close-btn" onClick={onClose}></button>
                </div>
                <div className="cart-products">
                    {items.length === 0 && (
                        <span className="empty-text">
                            Your basket is currently empty
                        </span>
                    )}
                    {items.map((item) => (
                        <div className="cart-product" key={item.id}>
                            <img src={item.image} alt={item.name} />
                            <div className="product-info">
                                <h3>{item.name}</h3>
                                <span className="product-price">
                                    £{item.price * item.count}
                                </span>
                            </div>
                            <select
                                className="count"
                                value={item.count}
                                onChange={(event) => {
                                    onQuantityChange(item.id, event.target.value);
                                }}
                            >
                                {[...Array(10).keys()].map((number) => {
                                    const num = number + 1;
                                    return (
                                        <option value={num} key={num}>
                                            {num}
                                        </option>
                                    );
                                })}
                            </select>
                            <button
                                className="btn remove-btn"
                                onClick={() => onProductRemove(item)}
                            ></button>
                        </div>
                    ))}
                    {items.length > 0 && (
                        <button className="btn checkout-btn">
                            Proceed to checkout
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Cart;







