import '../CSSContents/Shop.css'
import React, { useState } from 'react'

function Shop() {

    const items = [
        { id: 1, name: 'T-shirt', price: 25, image: 'images/shop/t-shirt.png' },
        { id: 2, name: 'slippers', price: 20, Image: 'images/shop/slippers.png' },
        { id: 3, name: 'patch', price: 30, image: 'images/shop/patch.png' },
        { id: 4, name: 'cap', price: 40, image: 'images/shop/cap.png' },
        { id: 5, name: 'bandana', price: 50, image: 'images/shop/bandana.png' },
        { id: 6, name: 'bag', price: 70, image: 'images/shop/bag.png' },
    ];

    function ShoppingPage() {
        const [cart, setCart] = useState([]);

        const addToCart = (item) => {
            setCart([...cart, item]);
        };

        return (
            <div className="Shop">

                <h1>Shopping Page</h1>
                <div className="item-list">
                    {items.map((item) => (
                        <div key={item.id} className="item">
                            <img src={item.image} alt={item.name} />
                            <h3>{item.name}</h3>
                            <p>${item.price}</p>
                            <button onClick={() => addToCart(item)}>Add to Cart</button>
                        </div>
                    ))}
                </div>
                <div className="cart">
                    <h2>Shopping Cart</h2>
                    {cart.map((item) => (
                        <div key={item.id} className="cart-item">
                            <p>{item.name}</p>
                            <p>${item.price}</p>
                        </div>
                    ))}
                </div>
            </div>




        )
    }
}
export default Shop;
