import '../Shop-page/Shop.css'
import { PRODUCTS } from './products';
import { Product } from './product';

function Shop() {
    //using map function to grab all the data from items
    return (
        <div className='Shop'>
            <div className='shopTitle'>
                <h1>Clean Energy For Business Shopping Page</h1>
            </div>

            <div className='items'>
                {PRODUCTS.map((product) => (
                    <Product data={product} />
                ))}

            </div>
        </div>
    );
}

export default Shop;

