import '../CSSContents/Shop.css'
//import React, { useState } from 'react'

function Shop() {

    const items = [
        { id: 1, name: 'T-shirt', price: 25, image: 'images/shop/t-shirt.png' },
        { id: 2, name: 'Patch', price: 10, image: 'images/shop/patch.png' },
        { id: 3, name: 'Cap', price: 20, image: 'images/shop/cap.png' },
        { id: 4, name: 'Bandana', price: 15, image: 'images/shop/bandana.png' },
        { id: 5, name: 'Bag', price: 20, image: 'images/shop/bag.png' },
        { id: 6, name: 'Socks', price: 20, image: 'images/shop/socks.png' }
    ];


    const [cartsVisibilty, setCartVisible] =
        useState(false);
    const [productsInCart, setProducts] =
        useState(
            JSON.parse(
                localStorage.getItem(
                    "shopping-cart"
                )
            ) || []
        );
    useEffect(() => {
        localStorage.setItem(
            "shopping-cart",
            JSON.stringify(productsInCart)
        );
    }, [productsInCart]);
    const addProductToCart = (product) => {
        const newProduct = {
            ...product,
            count: 1,
        };
        setProducts([
            ...productsInCart,
            newProduct,
        ]);
    };

    const onQuantityChange = (
        productId,
        count
    ) => {
        setProducts((oldState) => {
            const productsIndex =
                oldState.findIndex(
                    (item) =>
                        item.id === productId
                );
            if (productsIndex !== -1) {
                oldState[productsIndex].count =
                    count;
            }
            return [...oldState];
        });
    };

    const onProductRemove = (product) => {
        setProducts((oldState) => {
            const productsIndex =
                oldState.findIndex(
                    (item) =>
                        item.id === product.id
                );
            if (productsIndex !== -1) {
                oldState.splice(productsIndex, 1);
            }
            return [...oldState];
        });
    };











    return (
        <div className="Shop">

            <h1>Shopping Page</h1>
            <div className="item-list">
                {items.map((item) => (
                    <div key={item.id} className="item">
                        <img src={item.image} alt={item.name} />
                        <h3>{item.name}</h3>
                        <p>${item.price}</p>
                        <button className='cart-btn'>Add to Cart</button>
                    </div>
                ))}
            </div>


        </div>



    )

}


export default Shop;





