import React, { useState } from "react";

function co2Calculator() {
    const [electricityUsage, setElectricityUsage] = useState(0);
    const [gasUsage, setGasUsage] = useState(0);
    const [travelDistance, setTravelDistance] = useState(0);
    const [numOfEmployees, setNumOfEmployees] = useState(0);
    const [result, setResult] = useState(0);

    const handleCalculation = () => {
        const electricityCO2 = electricityUsage * 0.5;
        const gasCO2 = gasUsage * 0.2;
        const travelCO2 = travelDistance * 0.1;
        const employeeCO2 = numOfEmployees * 2;
        setResult(electricityCO2 + gasCO2 + travelCO2 + employeeCO2);
    };

    return (
        <div>
            <div>
                <label htmlFor="electricityUsage">Electricity Usage (kWh):</label>
                <input
                    type="number"
                    id="electricityUsage"
                    value={electricityUsage}
                    onChange={(e) => setElectricityUsage(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="gasUsage">Gas Usage (kWh):</label>
                <input
                    type="number"
                    id="gasUsage"
                    value={gasUsage}
                    onChange={(e) => setGasUsage(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="travelDistance">Travel Distance (km):</label>
                <input
                    type="number"
                    id="travelDistance"
                    value={travelDistance}
                    onChange={(e) => setTravelDistance(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="numOfEmployees">Number of Employees:</label>
                <input
                    type="number"
                    id="numOfEmployees"
                    value={numOfEmployees}
                    onChange={(e) => setNumOfEmployees(e.target.value)}
                />
            </div>
            <button onClick={handleCalculation}>Calculate CO2 Usage</button>
            <div>CO2 Usage: {result} kg</div>
        </div>
    );
};




export default co2Calculator;
