import "../CSSContents/ForgetPassword.css";
import { useState } from "react";
import axios from 'axios';

function ForgetPassword() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/forgetPassword', { email });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response.data.message);
    }
  };

  return (
    <div className="ForgetPassword">
      <form onSubmit={handleSubmit}>
        <h2>Forget Password</h2>
        <p>Enter your email address below and we will send you a link to reset your password.</p>
        <div className="form-group">
          <label>Email:</label>
          <input type="email" className="form-control" placeholder="Enter email" value={email} onChange={(event) => setEmail(event.target.value)} />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
        {message && <p className="message">{message}</p>}
      </form>
    </div>
  );
}

export default ForgetPassword;
