import '../CSSContents/SignUp.css';
import { Link } from 'react-router-dom';
import Axios from 'axios';
import { useState, useEffect } from "react";


function SignUp() {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [signUpStatus, setSignUpStatus] = useState("");

    const register = (e) => {
        e.preventDefault();
        Axios.post("http://localhost:3306/register", {

            username: username,
            email: email,
            password: password,

        }).then((response) => {
            if (response.data.message) {
                setSignUpStatus(response.data.message);
            } else {
                setSignUpStatus("Account created successfully! ");
            }
        })
    }

    return (
        <div classname='container'>
            <div className='signup-form'>
                <form>
                    <h4>SignUp Here!</h4>
                    <label>Username</label>
                    <input
                        className='text-input'
                        type='username'
                        name='username'
                        placeholder='Enter your Username'
                        required
                        onChange={(e) => { setUsername(e.target.value) }}
                    >
                    </input>
                    <label>Email</label>
                    <input
                        className='text-input'
                        type='text'
                        name='email'
                        placeholder='Enter your Email'
                        required
                        onChange={(e) => { setEmail(e.target.value) }}
                    >
                    </input>
                    <label>Password</label>
                    <input
                        className='text-input'
                        type='password'
                        name='password'
                        placeholder='Enter your username'
                        required
                        onChange={(e) => { setPassword(e.target.value) }}
                    >
                    </input>
                    <button className="fluid ui button blue"
                        onClick={register}

                    >Sumbit</button>
                    <span className='form-input-login'> Already have an account? Login
                        <Link to='/Login'> Here </Link>
                    </span>
                    <h1 style={{
                        color: 'red', fontsize: '15px', textAllign: 'center', margin: '20px'
                    }}>{signUpStatus}</h1>
                </form>
            </div>
        </div>
    );
}

/*
 <button className="fluid ui button blue">Sumbit</button>
                    <span className='form-input-login'> Already have an account? Login
                        <Link to='/Login'> Here </Link>
                    </span>
*/

export default SignUp;
