import '../CSSContents/Cart.css'
import React from 'react';

const Cart = ({ cartItems }) => {
    return (
        <div>
            <h1>Cart Page</h1>
            <div className="cart-items">
                {cartItems.map((item) => (
                    <div key={item.id} className="cart-item">
                        <h2>{item.name}</h2>
                        <p>{item.price}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Cart;


