import '../CSSContents/Shop.css'
import React, { useState } from 'react'
import Cart from './Cart';

function Shop() {

    const items = [
        { id: 1, name: 'T-shirt', price: 25, image: 'images/shop/t-shirt.png' },
        { id: 2, name: 'Patch', price: 10, image: 'images/shop/patch.png' },
        { id: 3, name: 'Cap', price: 20, image: 'images/shop/cap.png' },
        { id: 4, name: 'Bandana', price: 15, image: 'images/shop/bandana.png' },
        { id: 5, name: 'Bag', price: 20, image: 'images/shop/bag.png' },
        { id: 6, name: 'Socks', price: 20, image: 'images/shop/socks.png' }
    ];



    const [cartItems, setCartItems] = useState([
        {
            id: 1,
            name: "Product 1",
            price: 10,
            count: 1,
            image: "product1.jpg",
        },
        {
            id: 2,
            name: "Product 2",
            price: 20,
            count: 2,
            image: "product2.jpg",
        },
    ]);

    const handleProductRemove = (item) => {
        const updatedItems = cartItems.filter((i) => i.id !== item.id);
        setCartItems(updatedItems);
    };

    const handleQuantityChange = (itemId, newCount) => {
        const updatedItems = cartItems.map((item) =>
            item.id === itemId ? { ...item, count: parseInt(newCount, 10) } : item
        );
        setCartItems(updatedItems);
    };

    const handleCartClose = () => {
        console.log("Cart closed");
    };

    return (
        <div>
            <h1>My Shop</h1>
            <div className="products">
                <h2>Products</h2>
                <ul>
                    <li>Product 1 - £10</li>
                    <li>Product 2 - £20</li>
                </ul>
                <button>Add to cart</button>
            </div>
            <Cart
                items={cartItems}
                onProductRemove={handleProductRemove}
                onQuantityChange={handleQuantityChange}
                onClose={handleCartClose}
            />
        </div>
    );
}



export default Shop;









