import '../CSSContents/Cart.css'

function Cart({
    visibilty,
    items,
    onProductRemove,
    onClose,
    onQuantityChange, }) {

    return (
        <div className='Cart'>

            <div>
                <h2>Shopping Cart</h2>
                <button
                    className="btn close-btn"
                    onClick={onClose}>
                </button>
            </div>

            <div className='cart-item'>
                {items.length === 0 && (<span className='empty-text'>no item is selected</span>)}
                {items.map((item) => (
                    <div className="cart-product"
                        key={item.id}>
                        <img
                            src={
                                item.image
                            }
                            alt={item.name}
                        />
                        <div className="product-info">
                            <h3>
                                {product.name}
                            </h3>
                            <span className="product-price">
                                {product.price *
                                    product.count}
                                $
                            </span>
                        </div>
                        <select
                            className="count"
                            value={
                                product.count
                            }
                            onChange={(
                                event
                            ) => {
                                onQuantityChange(
                                    product.id,
                                    event
                                        .target
                                        .value
                                );
                            }}>
                            {[
                                ...Array(
                                    10
                                ).keys(),
                            ].map(
                                (number) => {
                                    const num =
                                        number +
                                        1;
                                    return (
                                        <option
                                            value={
                                                num
                                            }
                                            key={
                                                num
                                            }>
                                            {
                                                num
                                            }
                                        </option>
                                    );
                                }
                            )}
                        </select>
                        <button
                            className="btn remove-btn"
                            onClick={() =>
                                onProductRemove(
                                    product
                                )
                            }>
                            <RiDeleteBin6Line
                                size={20}
                            />
                        </button>
                    </div>
                ))}
                {products.length > 0 && (
                    <button className="btn checkout-btn">
                        Proceed to checkout
                    </button>
                )}
            </div>


        </div>

    )
}




export default Cart;
