import "../CSSContents/ForgetPassword.css";
import { useState } from "react";
import axios from 'axios';

function ForgetPassword() 
{
    const [form, setForm] = useState({
        username: '',
        email: '',
        password: '',
    });

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });

    };

    const validateInfo = (form) => {
        let errors = {};
    if (!form.email) {
        errors.email = 'Email is required!';
    } else if (!/\S+@\S+\.\S+/.test(form.email)) {
        errors.email = 'Email address is invalid';
    }
    if (!form.password) {
        errors.password = 'Password is required!';
    } else if (form.password.length < 6) {
        errors.password = 'Password must be at least 6 characters long!';
    }
    return errors;
};

const handleSubmit = (e) => {
    e.preventDefault();

    const errors = validateInfo(form)
    setErrors(errors);
  
    axios.post('http://localhost:4000/ForgetPassword', form)
    .then((response) => {
        if (response.data === 'Succesfull password reset') {
            setMessage('Succesfull password reset');
        } else 
        {
            setMessage('Login failed; please double-check your input details or create a new account.');
        }

    })

    return (
        <div className="ForgetPassword">
            <div className="Form">
                <form onSubmit={handleSubmit}>

                            <label>Email</label>
                            <input
                                type="email"
                                name="email"
                                placeholder="Email"
                                value={form.email}
                                onChange={handleChange}
                            />
                            {errors.email && <p className="error-message" > {errors.email}</p>}
                    
                    <label>Password</label>
                    <input
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={form.password}
                        onChange={handleChange}
                    />
                    {errors.password && <p className="error-message" > {errors.password}</p>}

                    <button type="submit" className='btn'>
                        {mode === 'ForgetPassword'}
                    </button>

                    <button type="button" className='btn' onClick={() => setMode(mode === 'ForgetPassword')}>
                        Switch to {mode === 'login' ? 'Register' : 'Login'}
                    </button>
                </form>

            </div>
        </div>
    );
}
}

export default ForgetPassword;
