import '../CSSContents/Cart.css'
import React, { useState } from 'react'

function Cart() {


    return (
        <div>
            <h1>Cart</h1>
            <ul>
                {props.items.map((item) => (
                    <li key={item.id}>{item.name}</li>
                ))}
            </ul>
        </div>
    );

}

export default Cart;



