import '../CSSContents/SignUp.css';
import { Link } from 'react-router-dom';
import { useState, useEffect } from "react";


function SignUp() {
    const [usernaem, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [signUpStatus, setSignUpStatu] = useState("");
    const [loginStatus, setLooginStatus] = useState("");




    const validate = (values) => {
        const errors = {};
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
        if (!values.username) {
            errors.username = "User name is needed!"
        }
        if (!values.email) {
            errors.email = "email is needed!"
        } else if (!regex.test(values.email)) {
            errors.email = "This is not a valid email format!";
        }
        if (!values.password) {
            errors.password = "password is needed!"
        }
        return errors;
    };
    return (
        <div className='signUp'>
            <form >
                <h1>SignUp form </h1>
                <div className="ui divider"></div>
                <div className="uiform">
                    <div className="field">
                        <label>Username</label>
                        <input
                            type="text"
                            name="username"
                            placeholder="Username"
                            value={formValues.username}
                            onChange={(e) => { setUsername(e.target.value) }}
                        />
                    </div>
                    <p> {formErros.username}</p>
                    <div className="field">
                        <label>Email </label>
                        <input
                            type="email"
                            name="email"
                            placeholder="Email"
                            value={formValues.email}
                            onChange={(e) => { setUsername(e.target.value) }}
                        />
                    </div>
                    <p> {formErros.email}</p>
                    <div className="field">
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            placeholder="Password"
                            value={formValues.password}
                            onChange={(e) => { setUsername(e.target.value) }}
                        />
                    </div>
                    <p> {formErros.password}</p>
                    <button className="fluid ui button blue">Sumbit</button>
                    <span className='form-input-login'> Already have an account? Login
                        <Link to='/Login'> Here </Link>
                    </span>
                </div>
            </form>
        </div>
    );
}



export default SignUp;
