import '../CSSContents/Cart.css'

function Cart({
    visibilty,
    items,
    onProductRemove,
    onClose,
    onQuantityChange, }) {

    return (
        <div className='Cart'>

            <div>
                <h2>Shopping Cart</h2>
                <button
                    className="btn close-btn"
                    onClick={onClose}>
                </button>
            </div>

            <div className='cart-item'>
                {items.length === 0 && (<span className='empty-text'>no item is selected</span>)}

                {items.map((item) => (
                    <div key={item.id} className="item">
                        <img src={item.image} alt={item.name} />
                        <h3>{item.name}</h3>
                        <span>
                            Â£{item.price * item.count}
                        </span>
                        <select className='count' value={item.count} onChange={(e) => { onQuantityChange(item.id, e.target.value) }}></select>
                    </div>
                ))}

            </div>
        </div>
    )
}




export default Cart;
