import '../CSSContents/Cart.css'
import React from 'react';
import { useState } from 'react';

const Cart = (props) => {
    const [itemCount, setItemCount] = useState(props.cartItems.length);

    const handleAddItem = () => {
        setItemCount(itemCount + 1);
    };

    const handleRemoveItem = (id) => {
        const updatedItems = props.cartItems.filter((item) => item.id !== id);
        setItemCount(updatedItems.length);
        props.setCartItems(updatedItems);
    };

    return (
        <div className='Cart'>
            <h1>Cart Page</h1>
            <div className="cart-items">
                {props.cartItems.map((item) => (
                    <div key={item.id} className="cart-item">
                        <img style={{ width: "100px", height: "100px" }} src={item.image} alt={item.name} />
                        <h2>{item.name}</h2>
                        <p>{item.price}</p>
                        <button onClick={() => handleRemoveItem(item.id)}>-</button>
                    </div>
                ))}
            </div>
            <div>
                <button onClick={handleAddItem}>+</button>
                <p>{itemCount} items selected</p>
            </div>
        </div>
    );
};

export default Cart;


