import '../A-Shop/Shop.css'
import { useState } from 'react';
import Cart from '../Cart';

function Shop() {
    const [cartItems, setCartItems] = useState([]);

    const items = [
        { id: 1, name: 'T-shirt', price: 25, image: 'images/shop-image/t-shirt.png' },
        { id: 2, name: 'Patch', price: 10, image: 'images/shop-image/patch.png' },
    ];

    const addToCart = (item) => {
        setCartItems([...cartItems, item]);
    };

    return (
        <div className='Shop'>
            <h1>Shopping Page</h1>
            <div className="item-list">
                {items.map((item) => (
                    <div key={item.id} className="item">
                        <h2>{item.name}</h2>
                        <p>{item.price}</p>
                        <button onClick={() => addToCart(item)}>Add to Cart</button>
                    </div>
                ))}
            </div>
            <button onClick={() => console.log(cartItems)}>View Cart</button>
            <Cart cartItems={cartItems} />
        </div>
    );
}

export default Shop;








