import '../CSSContents/Shop.css'


function Shop() {
    const [items, setItems] = useState([
        { id: 1, name: 'Item 1', price: 10, image: 'https://example.com/item1.jpg' },
        { id: 2, name: 'Item 2', price: 20, image: 'https://example.com/item2.jpg' },
        { id: 3, name: 'Item 3', price: 30, image: 'https://example.com/item3.jpg' },
    ]);

    const addToCart = (item) => {
        setCart((prevCart) => [...prevCart, item]);
    };

    return (
        <div>
            {items.map((item) => (
                <div key={item.id}>
                    <img src={item.image} alt={item.name} />
                    <div>{item.name}</div>
                    <div>${item.price}</div>
                    <button onClick={() => addToCart(item)}>Add to cart</button>
                </div>
            ))}
        </div>
    );
}

function Cart() {
    const [cart, setCart] = useState([]);

    const removeFromCart = (item) => {
        setCart((prevCart) => prevCart.filter((cartItem) => cartItem.id !== item.id));
    };

    return (
        <div>
            <h2>Cart</h2>
            {cart.length === 0 ? (
                <div>Your cart is empty</div>
            ) : (
                cart.map((item) => (
                    <div key={item.id}>
                        <img src={item.image} alt={item.name} />
                        <div>{item.name}</div>
                        <div>${item.price}</div>
                        <button onClick={() => removeFromCart(item)}>Remove from cart</button>
                    </div>
                ))
            )}
        </div>
    );
}

export default Shop;






