import "../CSSContents/newLog.css";
import React, { useState } from "react";
import axios from 'axios';


function Login2() {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        email: '',
        isLogin: true,
    });

    const [user, setUser] = useState(null);

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.name]: event.target.value
        });
    };

    const handleLogin = (event) => {
        event.preventDefault();
        // authenticate the user here
        setUser({
            username: formData.username,
        });
    };

    const handleRegistration = (event) => {
        event.preventDefault();
        // register the user here
        setUser({
            username: formData.username,
        });
    };

    return (
        <div>
            {user ? (
                <>
                    <h2>Welcome {user.username}</h2>
                    <button onClick={() => setUser(null)}>Logout</button>
                </>
            ) : (
                <form onSubmit={formData.isLogin ? handleLogin : handleRegistration}>
                    <input
                        type="text"
                        placeholder="Username"
                        name="username"
                        onChange={handleChange}
                        value={formData.username}
                        required
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        name="email"
                        onChange={handleChange}
                        value={formData.email}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        onChange={handleChange}
                        value={formData.password}
                        required
                    />
                    <button type="submit">
                        {formData.isLogin ? 'Login' : 'Register'}
                    </button>
                    <p>
                        {formData.isLogin
                            ? 'Not a member yet? '
                            : 'Already a member? '}
                        <button
                            type="button"
                            onClick={() => setFormData({ ...formData, isLogin: !formData.isLogin })}
                        >
                            {formData.isLogin ? 'Sign up' : 'Login'}
                        </button>
                    </p>
                </form>
            )}
        </div>
    );
};


export default Login2;

