import "../CSSContents/newLog.css";
import React, { useState, useEffect } from "react";
import axios from 'axios';




function Login2() {

    //Saving the values in the form variable
    const [form, setForm] = useState({
        username: '',
        password: '',
    });
    //mode determines whether the form should display login or register
    const [mode, setMode] = useState('login');
    const [errors, setErrors] = useState({});
    //message is used to display any form of error to the user
    const [message, setMessage] = useState('');


    //it updates the user's values in case a change has been made 
    //used a spread operator to update the form by targeting .name and .value 
    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });

    };

    const validateInfo = (form) => {
        let errors = {};
        if (!form.username.trim()) {
            errors.username = 'Username is required!';
        }
        if (!form.password) {
            errors.password = 'Password is required';
        } else if (form.password.length < 6) {
            errors.password = 'Password must be at least 6 characters long!';
        }

        return errors;
    };



    const handleSubmit = (e) => {
        e.preventDefault();
        const errors = validateInfo(form);
        setErrors(errors);

        if (Object.keys(errors).length === 0) {
            axios.post('http://localhost:4000/login', form)
                .then((response) => {
                    if (response.data === 'Logged in successfully') {
                        setMessage('Logged in successfully');

                        window.location.href = '/';
                    } else {
                        setMessage('Login was unsuccessful');
                    }
                })
                .catch((error) => {
                    setMessage(error.message);
                });
        }
    };



    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={form.username}
                    onChange={handleChange}
                />
                {errors.username && <p> {errors.username}</p>}
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={form.password}
                    onChange={handleChange}
                />
                {errors.password && <p> {errors.password}</p>}

                <button type="submit">
                    Login
                </button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default Login2;







