function Login2() {


    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [loginStatus, setLoginStatus] = useState("");
    const [registerStatus, setRegisterStatus] = useState("");

    const login = (e) => {
        e.preventDefault();
        axios.post("http://localhost:3001/login", {
            username: username,
            password: password,
        }).then((response) => {
            if (response.data.message) {
                setLoginStatus(response.data.message)
            } else {
                setLoginStatus(response.data[0].email)
            }
        })
    }

    const register = (e) => {
        e.preventDefault();
        axios.post("http://localhost:3001/register", {
            username: username,
            email: email,
            password: password,
        }).then((response) => {
            if (response.data.message) {
                setRegisterStatus(response.data.message)
            } else {
                setRegisterStatus("Acoount created successfully")
            }
        })
    }

    return (
        <div className="App">
            <form >
                <h4>Login Here</h4>
                <label>Username</label>
                <input type="text" onChange={(e) => { setUsername(e.target.value) }} placeholder="your username" />
                <label>Password</label>
                <input type="password" onChange={(e) => { setPassword(e.target.value) }} placeholder="your password" />
                <button onChlick={login}>submit</button>
                <h1 >{loginStatus}</h1>
            </form>
            <br />
            <form>
                <h4>Register Here</h4>
                <label>Username</label>
                <input type="text" onChange={(e) => { setUsername(e.target.value) }} />


            </form>
        </div>
    );
};

export default Login2;



