import "../CSSContents/newLog.css";
import React, { useState } from "react";
import axios from 'axios';


function Login2() {

    //Saving the values in the form variable
    const [form, setForm] = useState({
        username: '',
        email: '',
        password: '',
    });
    //mode determines whether the form should disply login or register
    const [mode, setMode] = useState('login');
    //message is used to disply any form of error to the user
    const [message, setMessage] = useState('');

    //it updates the users values in case a change has been made 
    //used a spread operator to update the form by targeting .name and .value 
    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    };


    /*
        const validateInfo() {
            {
                let errors = {}
                //username used ,,,,,trim to eliminate the white spaces
                if (!form.username.trim()) {
                    errors.username = 'Users name is required!'
                }
                //email
                if (!form.email) {
                    errors.email = 'Email is required!'
                }
                else if (!/\S+@\S+\.\S+/.test(form.email)) {
                    errors.email = 'Email address is invalid';
                }
                if (!form.password) {
                    errors.password = 'Password is required'
                }
                else if (form.password.length < 6) {
                    errors.password = 'Password needs to be at least 6 character long!'
                }
                return errors;
            }
        }
    */

    //it is used when the form is submitted, sends message to the backend to either login or register
    //if successful/denied  response is saved in message
    const handleSubmit = (e) => {
        e.preventDefault();


        if (mode === 'login') {
            axios.post('http://localhost:4000/login', form)
                .then((response) => {
                    setMessage(response.data);
                })
                .catch((error) => {
                    setMessage(error.message);
                });
        } else {
            axios.post('http://localhost:4000/register', form)
                .then((response) => {
                    setMessage(response.data);
                })
                .catch((error) => {
                    setMessage(error.message);
                });
        }
    };

    return (
        <div className="App">
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="username"
                    placeholder="Username"
                    value={form.username}
                    onChange={handleChange}
                />
                {mode === 'register' && (
                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        value={form.email}
                        onChange={handleChange}
                    />
                )}
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={form.password}
                    onChange={handleChange}
                />
                <button type="submit">
                    {mode === 'login' ? 'Login' : 'Register'}
                </button>
                <button type="button" onClick={() => setMode(mode === 'login' ? 'register' : 'login')}>
                    Switch to {mode === 'login' ? 'Register' : 'Login'}
                </button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default Login2;
