import "./CSSContents/NavBarStyle.css";
import LogoutBtn from "./Pages/LogoutBtn";
import { NavLink } from "react-router-dom";
import { useState, useEffect } from "react";


function NavBar({ isLoggedIn }) {
    const [username, setUsername] = useState('');

    useEffect(() => {
        const isLoggedIn = JSON.parse(localStorage.getItem('isLoggedIn'));
        if (isLoggedIn) {
            setUsername(JSON.parse(localStorage.getItem('username')));
        }
    }, []);
    return (
        <nav>
            <NavLink to="/" id="title"> Clean energy for business</NavLink>

            <div>
                <ul id='navbar'>
                    <NavItem to='/' label='Home' />
                    <NavItem to='/Shop' label='Shop' />
                    <NavItem to='/Profile' label='Profile' />
                    <NavItem to='/co2' label='Co2Calculator' />
                </ul>

                {LogoutBtn ? LogoutBtn : null}
                {isLoggedIn && <p>Welcome, {username}!</p>}

            </div>
        </nav >
    );
}

function NavItem({ to, label }) {
    return (
        <li>
            <NavLink to={to} activeClassName='active'>
                {label}
            </NavLink>
        </li>
    );
}

export default NavBar;
