import '../CSSContents/Login.css';
import '../HomeContents/SignUp';
import '/GitHub/CleanEnergyForBusiness/cleanenergyforbusiness/Server/index';
import Axios from 'axios';
import { Link } from 'react-router-dom';
import { useState, useEffect } from "react";

function Login() {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const [loginStatus, setLoginStatus] = useState("");


    const logback = (e) => {
        e.preventDefault();
        Axios.post("http://localhost:3306/logback", {

            username: username,
            password: password,

        }).then((response) => {
            if (response.data.message) {
                setLoginStatus(response.data.message);
            } else {
                setLoginStatus(response.data[0].email);
            }
        })
    }

    return (
        <div classname='container'>
            <div className='signup-form'>
                <form>
                    <h4>Login</h4>
                    <label>Username</label>
                    <input
                        className='text-input'
                        type='username'
                        name='username'
                        placeholder='Enter your Username'
                        required
                        onChange={(e) => { setUsername(e.target.value) }}
                    >
                    </input>

                    <label>Password</label>
                    <input
                        className='text-input'
                        type='password'
                        name='password'
                        placeholder='Enter your username'
                        required
                        onChange={(e) => { setPassword(e.target.value) }}
                    >
                    </input>
                    <button className="fluid ui button blue"
                        onClick={logback}
                    >Sumbit</button>
                    <span className='form-input-login'> Already have an account? Login
                        <Link to='/SignUp'> Here </Link>
                    </span>
                    <h1 style={{
                        color: 'red', fontsize: '15px', textAllign: 'center', margin: '20px'
                    }}>{loginStatus}</h1>
                </form>
            </div>
        </div>
    );
}





export default Login;
