import '../CSSContents/signup,login.css'
import '../CSSContents/SignUp.css';
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';




//gets the values for each field 
const [values, setValues] = useState({
    username: '',
    email: '',
    password: ''
});
//uses for the error handling 
const [errors, setErros] = useState({});

//when something changes it updates the value 
const handleChange = e => {
    const { name, value } = e.target;
    setValues({
        ...values,
        [name]: value
    });
};






function SignUp() {
    const { handleChange, values } = useForm


    return (
        <div className='form-content'>
            <form className='form'>
                <h1>SignUp now using the form below!</h1>
                <div className='form-inputs'>
                    <label className='form-lable'>
                        Username
                    </label>
                    <input
                        type='text'
                        name='username'
                        className='form-input'
                        placeholder='Enter your Username'
                        value={values.username}
                        onChange={handleChange}
                    />
                </div>
                <div className='form-inputs'>
                    <label className='form-lable'>
                        Email
                    </label>
                    <input
                        type='email'
                        name='email'
                        className='form-input'
                        placeholder='Enter your email'
                        value={values.email}
                        onChange={handleChange}
                    />
                </div>
                <div className='form-inputs'>
                    <label className='form-lable'>
                        Password
                    </label>
                    <input
                        type='text'
                        name='username'
                        className='form-input'
                        placeholder='Enter your Password'
                        value={values.password}
                        onChange={handleChange}
                    />
                </div>
                <button className='form-input-btn'
                    type='submit'>SignUp
                </button>
                <span className='form-input-login'> Already have an account? Login
                    <Link to='/Login'> Here </Link>

                </span>
            </form>
        </div>
    );
}


export default SignUp;
